import { React, useState, useEffect, useLayoutEffect } from "react";
// import "./SuccessContent.css";
import Modal from "../Modal";
import UserContainer from "../UserContainer";
import { useSelector, useDispatch } from "react-redux";
import { del } from "../../Redux/slice";

const SuccessContent = ({ users, handelUpdate }) => {
  const dispatch = useDispatch();
  const [data, setData] = useState([]);
  const [inputValue, setInputValue] = useState("");
  const [initialValue, setInitialValue] = useState("");
  const [error, setError] = useState("");
  const [isOpenModal, setIsOpenModal] = useState(false);
  const [chooseItemIdx, setChooseItemIdx] = useState(-1);
  const reduxState = useSelector((state) => state.users.data);

  console.log('reduxState',reduxState);

  useEffect(() => {
    if (!data.length) {
      setData(users);
      // dispatch(getUsers(users));
    }
  });

  const handelInput = (e) => {
    setInputValue(e.target.value);
  };

  const handelDelete = (id) => {
    const arr = reduxState.filter((item) => {
      return item.login.uuid !== id;
    });
    setData(arr);
    dispatch(deleteUser(arr));
  };

  const chooseName = (e, item) => {
    if (!e.target.value) setError("Имя не можеть быть пустым");
    else setError("");
    const array = reduxState.map((el) => {
      if (item.login.uuid === el.login.uuid) item.name.first = e.target.value;
      return el;
    });

    // setData(array);
    // dispatch(getUsers(array));
  };

  const clearSearch = () => {
    setChooseItemIdx(-1);
    setInputValue("");
  };

  const onOpenModal = (name, idx) => {
    setInitialValue(name);
    setChooseItemIdx(idx);
    setIsOpenModal(true);
  };

  const onCloseModal = (item) => {
    const array = reduxState.map((el) => {
      if (item.login.uuid === el.login.uuid) item.name.first = initialValue;
      return el;
    });

    setIsOpenModal(false);
    setData(array);
    // dispatch(getUsers(array));
  };

  const onContinue = (name) => {
    console.log(name);
    if (!error.length) {
      setIsOpenModal(false);
      const array = reduxState.map((item, idx) => {
        if (idx === chooseItemIdx) {
          return {
            ...item,
            name: {
              ...item.name,
              first: name
            }
          };
        }
        return item;
      });
      console.log(array);
      // dispatch(getUsers(array));
      setData(array);
    }
  };

  return (
    <div className="root">
      <button
        onClick={() => {
          handelUpdate("https://randomuser.me/api/?results=30");
          clearSearch();
        }}
      >
        загрузить еще
      </button>
      <input value={inputValue} onInput={(e) => handelInput(e)} />
      {users?.map((item, idx) => {
        if (item?.name?.first.toLowerCase().includes(inputValue.toLowerCase()))
          return (
            <UserContainer
              item={item}
              idx={idx}
              handelDelete={handelDelete}
              onOpenModal={onOpenModal}
              key={item.login.uuid}
            />
          );
      })}
      <Modal
        isOpen={isOpenModal}
        value={data?.[chooseItemIdx]?.name.first}
        onClose={() => onCloseModal(data?.[chooseItemIdx])}
        chooseName={(e) => chooseName(e, data?.[chooseItemIdx])}
        onContinue={(name) => onContinue(name)}
        error={error}
      />
    </div>
  );
};

export default SuccessContent;
