import { createSlice } from "@reduxjs/toolkit";

export const slice = createSlice({
  name: "users",

  initialState: {
    data: []
  },

  reducers: {
    postUsers: (state, users) => {
      state.data = users.payload;
    },

    deleteItem: (state, user) => {
      const arr = state.data.filter(item => item.login.uuid !== user.login.uuid);
      state.data = arr;
      console.log(state.data);
    },

    changeName: (state, user, newName) => {
      const arr = state.data.map(item => {
        if (item.login.uuid === user.login.uuid) {
          return {
            ...item,
             name: {
              ...item.name,
              firstName: newName
             }
          }
        } 
        return item;
      })
      state.data = arr;
    }
  }
});

// Action creators are generated for each case reducer function
export const { postUsers, deleteItem, changeName } = slice.actions;

export default slice.reducer;
